// content tabs init
function initTabs() {
	jQuery('.commentTab').tabset({
		tabLinks: 'a',
		defaultTab: true
	});
}



// initialize fixed blocks on scroll
function initStickyScrollBlock() {
	jQuery('.menuHolder, .menuLHolder').stickyScrollBlock({
		setBoxHeight: true,
		activeClass: 'headerFixed',
		positionType: 'fixed',
		extraTop: function() {
			var totalHeight = 0;
			jQuery('0').each(function() {
				totalHeight += jQuery(this).outerHeight();
			});
			return totalHeight;
		}
	});
}



// backtop init
function initbackTop() {
	"use strict";

    var jQuerybackToTop = jQuery("#back-top");
    jQuery(window).on('scroll', function() {
        if (jQuery(this).scrollTop() > 100) {
            jQuerybackToTop.addClass('active');
        } else {
            jQuerybackToTop.removeClass('active');
        }
    });
    jQuerybackToTop.on('click', function(e) {
        jQuery("html, body").animate({scrollTop: 0}, 900);
    });
}



jQuery(window).on('load', function() {

	"use strict";


	initIsoTop();
	// IsoTop init
	function initIsoTop() {
		"use strict";

		var isotopeHolder = jQuery('.isoContentHolder'),
			win = jQuery(window);
		jQuery('.isoFiltersList a').on( "click", function(e){
			e.preventDefault();
			
			jQuery('.isoFiltersList li').removeClass('active');
			jQuery(this).parent('li').addClass('active');
			var selector = jQuery(this).attr('data-filter');
			isotopeHolder.isotope({ filter: selector });
		});
		jQuery('.isoContentHolder').isotope({
			itemSelector: '.isoCol',
			transitionDuration: '0.6s',
			masonry: {
				columnWidth: '.isoCol'
			}
		});
	}

	initPreLoader()
	// PreLoader init
	function initPreLoader() {
	    "use strict";

	    jQuery('#loader').delay(1000).fadeOut();
	}
});



